mapper.update:
update fiveup.di_studentplancomment


        p_id = #{studentPlanComment.pId},


        comment = #{studentPlanComment.comment},


        update_time = #{studentPlanComment.updateTime}


where id = #{studentPlanComment.id}

mapper.select:
select s.student_id, s.student_name, s.student_class_number, s.student_grade, ss.s_deyu, ss.s_zhiyu, ss.s_tiyu, ss.s_meiyu, ss.s_laoyu, dp.p_deyu, dp.p_zhiyu, dp.p_tiyu, dp.p_meiyu, dp.p_laoyu, dp.p_plan, dc.comment from fiveup.di_student s left join fiveup.di_studentscore ss on s.student_id =ss.student_id left join fiveup.di_studentplan dp on dp.p_id = s.student_id left join fiveup.di_studentplancomment dc on dc.p_id = dp.p_id
s.student_id=#{studentId}
and s.student_class_number= #{classId}
and s.student_grade= #{gradeId}
AND ( CAST(s.student_id AS CHAR) LIKE CONCAT('%', #{findKey}, '%') OR s.student_name LIKE CONCAT('%', #{findKey}, '%') )
order by student_id DESC;
