sql.where:


    bs.student_num like concat('%', #{studentNum}, '%')


    and bs.student_name like concat('%', #{studentName}, '%')


    and bs.gender = #{gender}


    and bs.school_id = #{schoolId}


    and bs.grade_id = #{gradeId}


    and bc.class_name like concat('%', #{className}, '%')

and bs.deleted = 0
